void precompute() {
    prec[1] = 0;
    queue<int> q;
    q.push(1);
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int z = 1; z <= u; z++) {
            int newv = u + u / z;
            if (newv <= 1000 and prec[newv] > prec[u] + 1) {
                prec[newv] = prec[u] + 1;
                q.push(newv);
            }
        }
    }
}
void solve() {
    int n, k;
    cin >> n >> k;
    vector<int> b(n), v(n), w(n);
    for (int i = 0; i < n; i++) cin >> b[i];
    for (int i = 0; i < n; i++) cin >> v[i];
    for (int i = 0; i < n; i++) {
        w[i] = prec[b[i]];
    }
    vector<int> dp(k + 1);
    for (int i = 0; i < n; i++) {
        for (int wt = k; wt >= w[i]; wt--) {
            dp[wt] = max(dp[wt], dp[wt - w[i]] + v[i]);
        }
    }
    cout << dp[k] << '\n';
}
